package Basicprogram;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=5; i++) {
			System.out.println("Value of i :" +i);
		}
		int i =1;
		while(i<=5) {
			System.out.println("value of i:" +i);
			i=i+1;
			
			
				
		}
		
		int i1=1;
		 do {
			System.out.println("value of i:" +i1);
			i1++;
			
		}while(i1<=5);
		
	}

}

